	, num2_ord
	, num3_ord
	, num4_ord
	, num5_ord
	, num6_ord
	, num7
	, nums
) VALUES (
	#{round}
	, 

		    null

		DATE '${dat}'

	, #{chucheomgi}
	, #{num1_ord}
	, #{num2_ord}
	, #{num3_ord}
	, #{num4_ord}
	, #{num5_ord}
	, #{num6_ord}
	, #{num7}
	, ARRAY [${num1}, ${num2}, ${num3}, ${num4}, ${num5}, ${num6}]

)

mapper.select:
SELECT
	DISTINCT ${column}
FROM
	LottoHistory
WHERE
	round in #{round} - #{item.diff}
ORDER BY	${column}
